    private void btnLanzaderasActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLanzaderasActionPerformed
        //se configura el boton lanzadera para que cambie el contenido del jpanel
        lanzaderaPanel lan = new lanzaderaPanel();
        lan.setSize(940, 600);
        lan.setLocation(0, 0);
        
        content.removeAll();
        content.add(lan, BorderLayout.CENTER);
        content.revalidate();
        content.repaint();
    }//GEN-LAST:event_btnLanzaderasActionPerformed

